SELECT DISTINCT
       PHCSTG.INSTIT,
       PHTTRC.NOMINT,
       PHTTRC.NUMINS,
       PHTTRC.ENDERE,
       PHTTRC.NUMERO,
       PHTTRC.BAIRRO,
       PHTTRC.NUMCEP,
       PHTTRC.CMPCEP,
       PHTTRC.CODMUN,
       PHTTRC.NUMDDD,
       PHTTRC.TELEF1
FROM PHCSTG
INNER JOIN PHTTRC
	ON PHCSTG.INSTIT = PHTTRC.CODTRC