SELECT PHTEMP.CODEMP,
	   PHTEMP2.ANOMES,
	   PHTEMP.ATICOD,
	   PHTEMP.NUMDDD,
	   PHTEMP.TELEFO,
	   PHTEMP.NUMINS,
	   PHTEMP.NUMCEI,
	   PHTEMP.NUMCAE,
	   PHTEMP.CODMUN,
	   PHTEMP.TIPLOG,
	   PHTEMP.ENDERE,
	   PHTEMP.NUMERO,
	   PHTEMP.COMPLE,
	   PHTEMP.BAIRRO,
	   PHTEMP.CIDADE,
	   PHTEMP.ESTADO,
	   PHTEMP.NUMCEP,
	   PHTEMP.CMPCEP,
	   PHTEMP2.GPSTER,
	   PHTEMP2.GPSACI,
	   PHTEMP2.GPSFPA,
	   PHTEMP2.SFPREC,
	   PHTEMP2.GPSPGT,
	   PHTEMP2.SFPCON,
	   PHTEMP2.FILANT,
	   PHTEMP2.SFPBAN,
	   PHTEMP2.SFPAGE
FROM PHTEMP
LEFT JOIN PHTEMP2
	ON PHTEMP2.CODEMP = PHTEMP.CODEMP
ORDER BY 1, 2