SELECT DISTINCT
       CONTRATOS.ESTABELECIMENTO,
       CARGOS.CARGO,
       CARGOS.NOME,
       CARGOS.CBO
FROM CARGOS
INNER JOIN CONTRATOS
	ON CONTRATOS.CARGO = CARGOS.CARGO

UNION

SELECT DISTINCT
       CONTRATOS.ESTABELECIMENTO,
       CARGOS.CARGO,
       CARGOS.NOME,
       CARGOS.CBO
FROM CARGOS
INNER JOIN ALTERACOES_DE_CONTRATO
	ON ALTERACOES_DE_CONTRATO.CARGO = CARGOS.CARGO
INNER JOIN CONTRATOS
	ON CONTRATOS.CONTRATO = ALTERACOES_DE_CONTRATO.CONTRATO
ORDER BY 1, 2

