SELECT DISTINCT
       CONTRATOS.ESTABELECIMENTO,
       CENTROS_DE_CUSTO.CENTRO_DE_CUSTO,
       CENTROS_DE_CUSTO.NOME
FROM CENTROS_DE_CUSTO
INNER JOIN CONTRATOS
	ON CONTRATOS.CENTRO_DE_CUSTO = CENTROS_DE_CUSTO.CENTRO_DE_CUSTO

UNION

SELECT DISTINCT
       CONTRATOS.ESTABELECIMENTO,
       CENTROS_DE_CUSTO.CENTRO_DE_CUSTO,
       CENTROS_DE_CUSTO.NOME
FROM CENTROS_DE_CUSTO
INNER JOIN ALTERACOES_DE_CONTRATO
	ON ALTERACOES_DE_CONTRATO.CENTRO_DE_CUSTO = CENTROS_DE_CUSTO.CENTRO_DE_CUSTO
INNER JOIN CONTRATOS
	ON CONTRATOS.CONTRATO = ALTERACOES_DE_CONTRATO.CONTRATO
ORDER BY 1, 2

