SELECT ESTABELECIMENTOS.ESTABELECIMENTO,
       ESOCIAL.TABELA_NOME,
       ESOCIAL.TABELA_CHAVE,
       ESOCIAL.VIGENCIA,
       ESOCIAL.RECIBO
FROM ESOCIAL
INNER JOIN ESTABELECIMENTOS
	ON ESTABELECIMENTOS.EMPRESA = ESOCIAL.EMPRESA
WHERE ESOCIAL.RECIBO <> '' AND 
      (ESOCIAL.CODIGO_EVENTO IN ('S-1000','S-1005','S-1020','S-1030','S-2200','S-2205','S-2206','S-2300') OR
	   ESOCIAL.TABELA_NOME IN ('cargos', 'pessoas', 'contratos'))
ORDER BY 1, 2