SELECT CONTRATOS.ESTABELECIMENTO,
       CONTRATOS.CONTRATO,
       PERIODOS_AQUISITIVOS.DATA_INICIAL,
       PERIODOS_AQUISITIVOS.DATA_FINAL,
       COALESCE(PERIODOS_AQUISITIVOS.FALTAS, 0) AS FALTAS,
       PERIODOS_AQUISITIVOS.PERIODO
  FROM PERIODOS_AQUISITIVOS
 INNER JOIN CONTRATOS
    ON CONTRATOS.CONTRATO = PERIODOS_AQUISITIVOS.CONTRATO
 ORDER BY 1, 2, 3;