SELECT FERIAS.ESTABELECIMENTO,
       FERIAS.CONTRATO,
       FERIAS.LANCAMENTO,
       PERIODOS_UTILIZADOS.PERIODO,
       FERIAS.GOZO_INICIAL,
       FERIAS.GOZO_FINAL,
       FERIAS.ABONO_INICIAL,
       FERIAS.ABONO_FINAL,
       FERIAS.DATA_AVISO,
       FERIAS.DATA_PAGAMENTO
  FROM FERIAS
 INNER JOIN PERIODOS_UTILIZADOS
    ON PERIODOS_UTILIZADOS.CONTRATO = FERIAS.CONTRATO
   AND PERIODOS_UTILIZADOS.FERIAS = FERIAS.LANCAMENTO
 ORDER BY 1, 2, 3;