SELECT DISTINCT
       CONTRATOS.ESTABELECIMENTO,
       HORARIOS_DE_TRABALHO.HORARIO,
       HORARIOS_DE_TRABALHO.NOME,
       HORARIOS_DE_TRABALHO.DESCRICAO,
       HORARIOS_DE_TRABALHO.DOMINGO,
       HORARIOS_DE_TRABALHO.SEGUNDA,
       HORARIOS_DE_TRABALHO.TERCA,
       HORARIOS_DE_TRABALHO.QUARTA,
       HORARIOS_DE_TRABALHO.QUINTA,
       HORARIOS_DE_TRABALHO.SEXTA,
       HORARIOS_DE_TRABALHO.SABADO
FROM HORARIOS_DE_TRABALHO
INNER JOIN CONTRATOS
	ON CONTRATOS.HORARIO = HORARIOS_DE_TRABALHO.HORARIO

UNION

SELECT DISTINCT
       CONTRATOS.ESTABELECIMENTO,
       HORARIOS_DE_TRABALHO.HORARIO,
       HORARIOS_DE_TRABALHO.NOME,
       HORARIOS_DE_TRABALHO.DESCRICAO,
       HORARIOS_DE_TRABALHO.DOMINGO,
       HORARIOS_DE_TRABALHO.SEGUNDA,
       HORARIOS_DE_TRABALHO.TERCA,
       HORARIOS_DE_TRABALHO.QUARTA,
       HORARIOS_DE_TRABALHO.QUINTA,
       HORARIOS_DE_TRABALHO.SEXTA,
       HORARIOS_DE_TRABALHO.SABADO
FROM HORARIOS_DE_TRABALHO
INNER JOIN ALTERACOES_DE_CONTRATO
	ON ALTERACOES_DE_CONTRATO.HORARIO = HORARIOS_DE_TRABALHO.HORARIO
INNER JOIN CONTRATOS
	ON CONTRATOS.CONTRATO = ALTERACOES_DE_CONTRATO.CONTRATO
ORDER BY 1, 2

