SELECT RECIBOS_DE_CONTRATO.ESTABELECIMENTO,
       RECIBOS_DE_CONTRATO.CONTRATO,
       RECIBOS_DE_CONTRATO."DATA" AS "DATA",
       EVENTOS_DE_RECIBO.EVENTO,
       EVENTOS_DE_RECIBO.VALOR_LANCADO,
       EVENTOS_DE_RECIBO.VALOR_APURADO
  FROM EVENTOS_DE_RECIBO
 INNER JOIN RECIBOS_DE_CONTRATO
    ON RECIBOS_DE_CONTRATO.RECIBO = EVENTOS_DE_RECIBO.RECIBO
 WHERE EVENTOS_DE_RECIBO.FOLHA = 2
   AND EVENTOS_DE_RECIBO.TIPO = '+'
   AND RECIBOS_DE_CONTRATO."DATA" > DATEADD(-4 YEAR TO CURRENT_DATE)
ORDER BY 1, 2, 3;