SELECT CONTRATOS.ESTABELECIMENTO,
       ALTERACOES_DE_CONTRATO.CONTRATO,
       ALTERACOES_DE_CONTRATO."DATA",
       ALTERACOES_DE_CONTRATO.DATA_REAJUSTE_SALARIO,
       ALTERACOES_DE_CONTRATO.SALARIO_BASE,
       ALTERACOES_DE_CONTRATO.MOTIVO_REAJUSTE_SALARIO,
       ALTERACOES_DE_CONTRATO.OCORRENCIA,
       ALTERACOES_DE_CONTRATO.CATEGORIA,
       ALTERACOES_DE_CONTRATO.TIPO_SALARIO
FROM ALTERACOES_DE_CONTRATO
LEFT JOIN CONTRATOS
	ON CONTRATOS.CONTRATO = ALTERACOES_DE_CONTRATO.CONTRATO
ORDER BY 1, 2, 3