SELECT DISTINCT
       func.CODIGOEMPRESA,
       c.CODIGOCARGO,
       c.DESCRCARGO,
       c.CBO
  FROM FUNCCARGO func JOIN CARGO c
    ON c.CODIGOCARGO = func.CODIGOCARGO
UNION
SELECT DISTINCT
       ter_ban.CODIGOEMPRESA,
       c.CODIGOCARGO,
       c.DESCRCARGO,
       c.CBO
  FROM TERCEIROBANCO ter_ban
  LEFT JOIN TERCEIRO ter
    ON ter.CODIGOTERC = ter_ban.CODIGOTERC
  LEFT JOIN CARGO c
    ON c.CODIGOCARGO = ter.CODIGOCARGO
 ORDER BY 1, 2
 
