SELECT FC.CODIGOEMPRESA,
       FC.CODIGOFUNCCONTR,
       FP.NOMEFUNC,
       FC.DATAADM,
       FP.CPFFUNC,
       FP.GRAUINSTR,
       FP.ENDERELETR
 FROM FUNCCONTRATO FC
 JOIN FUNCPESSOA FP ON FC.CODIGOFUNCPESSOA = FP.CODIGOFUNCPESSOA
 WHERE EXISTS(SELECT 1
                FROM FUNCCONTRATO AS epr
               WHERE epr.CODIGOEMPRESA = fc.CODIGOEMPRESA
                 AND epr.CODIGOSUPERVISORESTAGIO = fc.CODIGOFUNCCONTR)