SELECT CODIGOEMPRESA,
       CODIGOFUNCCONTR,
       CODIGODEPEND,
       NOMEDEPEND,
       DATANASC,
       IDADELIMSALFAM,
       IDADELIMIR,
       REPLACE(CARTORIO, ',', ' ') AS CARTORIO,
       LIVROREG,
       FOLHALIVROREG,
       NUMEROREG,
       ENTREGACERT,
       PARENTESCO,
       SEXO,
       CPFDEPEND,
       EHDEPENDENTEIR,
       NUMDECLARNASCVIVO,
       MATRICULA,
       mun.NOMEMUNIC
FROM DEPENDENTE dep
LEFT JOIN MUNICIPIO mun
    ON mun.CODIGOMUNIC = dep.CODIGOMUNIC
    AND mun.SIGLAESTADO = dep.SIGLAESTADO
ORDER BY CODIGOEMPRESA, CODIGOFUNCCONTR, CODIGODEPEND
