SELECT DISTINCT
       EVENTOEMP.CODIGOEMPRESA,
       EVENTO.CODIGOEVENTO,
       EVENTO.DESCREVENTO,
       EVENTO.CODIGOPROCED,
       EVENTO.REFEREVENTO,
       EVENTO.SALHORAEXTRA,
       EVENTO.SALADIC,
       EVENTO.SALCOMIS,
       EVENTO.SAL1TERCFERIAS,
       EVENTO.SALFIXO,
       PROCEDIMENTO.DESCRPROCED
  FROM EVENTOBASE
 INNER JOIN EVENTO
    ON EVENTO.CODIGOEVENTO = EVENTOBASE.CODIGOEVENTOUTIL
 INNER JOIN EVENTOEMP
    ON EVENTOEMP.CODIGOEVENTO = EVENTO.CODIGOEVENTO
 INNER JOIN PROCEDIMENTO
    ON PROCEDIMENTO.CODIGOPROCED = EVENTO.CODIGOPROCED
 ORDER BY 1, 2