SELECT s.*,
       m.NOMEMUNIC AS CIDADE,
       nasc.NOMEMUNIC AS LOCALNASCTO,
       org.SIGLAORGAOEMISSOR AS ORGAOEMISSORRG
  FROM SOCIO s
  LEFT JOIN MUNICIPIO m
    ON m.CODIGOMUNIC = s.CODIGOMUNICNASC
   AND m.SIGLAESTADO = s.SIGLAESTADONASC
  LEFT JOIN MUNICIPIO nasc
    ON nasc.CODIGOMUNIC = s.CODIGOMUNICNASC
   AND nasc.SIGLAESTADO = s.SIGLAESTADONASC
  LEFT JOIN ORGAOEMISSOR org
    ON org.CODIGOORGAOEMISSOR = s.CODIGOORGAOEMISSORRG
 ORDER BY s.CODIGOEMPRESA,
          s.CODIGOSOCIO,
          s.DATAINICIAL