SELECT b.CODIGOBANCO,
       b.NOMEBANCO,
       a.NUMEROAGENCIA,
       a.DIGITOAGENCIA,
       a.NOMEAGENCIA,
       REPLACE(l.NOMELOGRADORO, ',', ' ') AS LOGRADORO_AGENCIA,
       REPLACE(a.ENDERECOAGENCIA, ',', ' ') AS ENDERECOAGENCIA,
       a.NUMENDERAGENCIA,
       a.BAIRROENDERAGENCIA
  FROM AGENCIA a
  LEFT JOIN BANCO b
    ON b.CODIGOBANCO = a.CODIGOBANCO
  LEFT JOIN LOGRADORO l
    ON l.CODIGOLOGRADORO = a.CODIGOTIPOLOGRAD
 ORDER BY b.CODIGOBANCO,
          a.NUMEROAGENCIA