SELECT c.CODIGOCONT,
       c.NOMECONT,
       REPLACE(l.NOMELOGRADORO,',',' ') AS LOGRADORO_CONTADORES,
       REPLACE(c.ENDERECOCONT,',',' ') AS ENDERECOCONT,
       c.COMPLENDERCONT,
	   c.NUMENDERCONT,
       c.BAIRROENDERCONT,
       c.SIGLAESTADO,
       c.CODIGOMUNIC,
       c.CEPENDERCONT,
       c.DDDFONE,
       c.NUMEROFONE,
	   c.DDDFAX,
       c.NUMEROFAX,
       c.TIPOINSCR,
       c.INSCRFEDERAL,
       c.NUMEROREG,
	   c.UFCRC,
       c.NUMSEQCRC,
       c.NUMERORG,
       c.DATARG,
       c.ORGAOEMISSORRG,
       c.SIGLAESTADORG,
       c.EMAIL,
       m.NOMEMUNIC,
	   m.CODIGORAIS
  FROM CONTADOR c
  LEFT JOIN LOGRADORO l
    ON l.CODIGOLOGRADORO = c.CODIGOTIPOLOGRAD
  LEFT JOIN MUNICIPIO m
    ON m.CODIGOMUNIC = c.CODIGOMUNIC
   AND m.SIGLAESTADO = c.SIGLAESTADO
 ORDER BY c.CODIGOCONT