SELECT CODIGOEMPRESA,
       CODIGOFUNCCONTR,
       DATAINICIALFERIAS,
       CAST(DATAFINALFERIAS - DIASABONO * INTERVAL '1 day' AS DATE) AS DATAFINALFERIAS,
       CAST(DIASABONO AS INTEGER) AS DIASABONO,
       DATAABONO,
       CAST(DATAABONO + DIASABONO * INTERVAL '1 day' - INTERVAL '1 day' AS DATE) AS DATAFINALABONO,
       DATAPGTO,
       PAGAADTO13,
       DATAINICIAL,
       TIPORECIBO
  FROM RECIBOFERIAS
 ORDER BY CODIGOEMPRESA,
          CODIGOFUNCCONTR,
          DATAINICIALFERIAS
