SELECT emp.CODIGOEMPRESA,
       e.CODIGOESTAB,
       REPLACE(emp.NOMEEMPRESA, ',', ' ') AS NOMEEMPRESA,
       REPLACE(e.NOMEFANTASIA, ',', ' ') AS NOMEFANTASIA,
       e.CODIGOTIPOLOGRAD,
       REPLACE(e.ENDERECOESTAB, ',', ' ') AS ENDERECOESTAB,
       e.NUMENDERESTAB,
       REPLACE(e.COMPLENDERESTAB, ',', ' ') AS COMPLENDERESTAB,
       REPLACE(e.BAIRROENDERESTAB, ',', ' ') AS BAIRROENDERESTAB,
       e.SIGLAESTADO,
       e.CODIGOMUNIC,
       e.CEPENDERESTAB,
       e.DDDFONE,
       e.NUMEROFONE,
       e.DDDFAX,
       e.NUMEROFAX,
       e.EMAIL,
       e.INSCRMUNIC,
       e.INSCRESTAD,
       e.CODIGOATIVFEDERAL,
       REPLACE(e.DESCRATIVFEDESTAB, ',', ' ') AS DESCRATIVFEDESTAB,
       e.CODIGONATURJURID,
       e.INSCRFEDERAL,
       e.NUMEROREGIST,
       e.DATAREGIST,
       e.DATAINICIOATIV,
       mun.NOMEMUNIC,
       mun.CODIGOFEDERAL,
       mun.CODIGORAIS,
       e.CAPITALSOCIAL,
       e.VALORNOMINALCOTAS,
       e.INSCRCAEPF,
       e.CPFRESPCNO
  FROM EMPRESA emp
  LEFT JOIN ESTAB e
    ON e.CODIGOEMPRESA = emp.CODIGOEMPRESA
  LEFT JOIN municipio mun
    ON mun.CODIGOMUNIC = e.CODIGOMUNIC
   AND mun.SIGLAESTADO = e.SIGLAESTADO
 ORDER BY emp.CODIGOEMPRESA,
          e.CODIGOESTAB