SELECT DISTINCT
       fe.CODIGOEMPRESA,
       ed.CODIGOESCALA,
       ed."data" AS "DATA",
       ed.CODIGOHORARIO,
       e.DESCRESCALA,
       e.CARGAHORMENSAL,
       e.CARGAHORSEMANAL,
       e.CARGAHORDIARIA
  FROM FUNCESCALA fe
  LEFT JOIN ESCALADETALHE ed
    ON ed.CODIGOESCALA = fe.CODIGOESCALA
  LEFT JOIN ESCALA e
    ON ed.CODIGOESCALA = e.CODIGOESCALA
 ORDER BY 1, 2