SELECT sind.*,
       conv.PERCADICNOT,
       conv.MESDATABASE,
	   mun.NOMEMUNIC
FROM SINDICATO sind
LEFT JOIN SINDCONVENCAO conv
	ON conv.CODIGOSIND = sind.CODIGOSIND
   	AND conv.DATAINICIAL = (SELECT MAX(conv2.DATAINICIAL)
                             FROM SINDCONVENCAO conv2
                            WHERE conv2.CODIGOSIND = conv.CODIGOSIND)
LEFT JOIN MUNICIPIO mun
    ON mun.CODIGOMUNIC = sind.CODIGOMUNIC
    AND mun.SIGLAESTADO = sind.SIGLAESTADO
WHERE sind.CODIGOSIND > 0
ORDER BY sind.CODIGOSIND