SELECT QTDEVTRANSPDET.CODIGOEMPRESA,
       QTDEVTRANSPDET.CODIGOFUNCCONTR,
       QTDEVTRANSP.DATAINICIAL,
       QTDEVTRANSP.DATAFINAL,
       QTDEVTRANSPDET.CODIGOLINHAVT,
       SUM(QTDEVTRANSPDET.QTDEPASSE) AS QUANTIDADE
FROM QTDEVTRANSPDET
INNER JOIN QTDEVTRANSP
	ON QTDEVTRANSPDET.CODIGOEMPRESA = QTDEVTRANSP.CODIGOEMPRESA
	AND QTDEVTRANSPDET.CODIGOFUNCCONTR = QTDEVTRANSP.CODIGOFUNCCONTR
	AND QTDEVTRANSPDET.COMPET = QTDEVTRANSP.COMPET
GROUP BY 1, 2, 3, 4, 5
ORDER BY 1, 2, 3