SELECT CODCOLIGADA,
       '' AS CODIGO,
       TIPOEVENTO,
       RECIBO,
       '' AS CODIGO_ESOCIAL
FROM PESOCIALEVENTOS
WHERE TIPOEVENTO IN ('S-1000')
AND STATUS = 10
AND RECIBO IS NOT NULL

UNION

SELECT PESOCIALEVENTOS.CODCOLIGADA,
       TO_CHAR(PFUNCAO.CODIGO) AS CODIGO,
       PESOCIALEVENTOS.TIPOEVENTO,
       PESOCIALEVENTOS.RECIBO,
       TO_CHAR(PESOCIALEVENTOS.CODFUNCAO) AS CODIGO_ESOCIAL
FROM PESOCIALEVENTOS
INNER JOIN PFUNCAO
ON PFUNCAO.CODCOLIGADA = PESOCIALEVENTOS.CODCOLIGADA
AND PFUNCAO.CODIGO = PESOCIALEVENTOS.CODFUNCAO
WHERE TIPOEVENTO IN ('S-1030')
AND STATUS = 10
AND RECIBO IS NOT NULL

UNION

SELECT PESOCIALEVENTOS.CODCOLIGADA,
       TO_CHAR(PESOCIALEVENTOS.CHAPA) AS CODIGO,
       PESOCIALEVENTOS.TIPOEVENTO,
       PESOCIALEVENTOS.RECIBO,
       TO_CHAR(PFUNC.MATRICULAESOCIAL) AS CODIGO_ESOCIAL
FROM PESOCIALEVENTOS
INNER JOIN PFUNC
ON PFUNC.CODCOLIGADA = PESOCIALEVENTOS.CODCOLIGADA
AND PFUNC.CHAPA = PESOCIALEVENTOS.CHAPA
WHERE TIPOEVENTO IN ('S-2200', 'S-2300')
AND STATUS = 10
AND RECIBO IS NOT NULL