SELECT PFHSTFCO.CODCOLIGADA,
       PFHSTFCO.CHAPA,
       PFHSTFCO.DTMUDANCA,
       PFUNCAO.CODIGO AS CARGO
FROM PFHSTFCO
INNER JOIN PFUNCAO
ON PFHSTFCO.CODCOLIGADA = PFUNCAO.CODCOLIGADA
AND PFHSTFCO.CODFUNCAO = PFUNCAO.CODIGO
ORDER BY PFHSTFCO.CODCOLIGADA, PFHSTFCO.CHAPA, PFHSTFCO.DTMUDANCA, PFUNCAO.CODIGO;
