SELECT PFHSTSEC.CODCOLIGADA,
       PFHSTSEC.CHAPA,
       PFHSTSEC.DTMUDANCA,
       PSECAO.CODIGO AS SERVICO
FROM PFHSTSEC
INNER JOIN PSECAO
ON PFHSTSEC.CODCOLIGADA = PSECAO.CODCOLIGADA
AND PFHSTSEC.CODSECAO = PSECAO.CODIGO
ORDER BY PFHSTSEC.CODCOLIGADA, PFHSTSEC.CHAPA, PFHSTSEC.DTMUDANCA, PSECAO.CODIGO;
