SELECT PFDEPEND.CODCOLIGADA,
       PFUNC.chapa AS CODIGO_FUNCIONARIO,
       PFDEPEND.NRODEPEND,
       PFDEPEND.NOME,
       PFDEPEND.DTNASCIMENTO,
       PFDEPEND.CPF,
       PFDEPEND.LOCALNASCIMENTO,
       PFDEPEND.GRAUPARENTESCO,
       PFDEPEND.CARTORIO,
       PFDEPEND.NROREGISTRO,
       PFDEPEND.NROLIVRO,
       PFDEPEND.NROFOLHA,
       PFDEPEND.DATAENTREGACERTIDAO,
       PFDEPEND.SEXO,
	   PFDEPEND.INCIRRF
FROM [dbo].PFDEPEND
INNER JOIN [dbo].PFUNC
ON PFDEPEND.CODCOLIGADA = PFUNC.CODCOLIGADA
AND PFDEPEND.CHAPA = PFUNC.CHAPA 
ORDER BY 1,2