SELECT PFUNC.CODCOLIGADA,
	   PFUNC.CHAPA,
	   PFUNC.NOME,
	   PFUNC.CODSINDICATO,
	   PFUNC.CODHORARIO,
	   PFUNC.SALARIO,
	   PFUNC.DTOPCAOFGTS,
	   PFUNC.CONTAFGTS,
	   PFUNC.CONTRIBSINDICAL,
	   PFUNC.TIPOADMISSAO,
	   PFUNC.DATADEMISSAO,
	   PFUNC.PISPARAFGTS,
	   PFUNC.PISPASEP,
	   PFUNC.DTCADASTROPIS,
	   PFUNC.CODBANCOFGTS,
	   PFUNC.CODCATEGORIA,
	   PFUNC.CLASSECONTRIB,
	   PFUNC.DATAADMISSAO,
	   PFUNC.CODFILIAL,
	   PFUNC.TIPOADMISSAO,
	   PFUNC.CONTAPAGAMENTO,
	   PFUNC.OPBANCARIA,
	   PFUNC.MATRICULAESOCIAL,
	   PFUNC.CODRECEBIMENTO,
	   PFUNC.CODSITUACAO,
	   PFUNC.DTDESLIGAMENTO,
	   PFUNC.DTDESLIGAMENTOREINT,
	   PFUNC.FIMPRAZOCONTR,
	   PFUNC.DTVENCFERIAS,
	   PFUNC.DTTRANSFERENCIA,
	   PFUNC.CODCOLIGADAORIGEM,
	   PFUNC.CHAPAORIGEM,
	   PFUNC.CODBANCOPAGTO,
	   PFUNC.CODAGENCIAPAGTO,
	   PFUNC.CODBANCOPIS,
	   PFUNC.VINCULORAIS,
	   PFUNC.CODOCORRENCIA,
	   PFUNC.JORNADAMENSAL,
	   PFUNC.CODCATEGORIAESOCIAL,
	   PPESSOA.DTNASCIMENTO,
	   PPESSOA.ESTADOCIVIL,
	   PPESSOA.SEXO,
	   PPESSOA.NACIONALIDADE,
	   PPESSOA.GRAUINSTRUCAO,
	   PPESSOA.RUA,
	   PPESSOA.CODTIPORUA,
	   PPESSOA.NUMERO,
	   PPESSOA.COMPLEMENTO,
	   PPESSOA.BAIRRO,
	   PPESSOA.ESTADO,
	   PPESSOA.CIDADE,
	   PPESSOA.CEP,
	   PPESSOA.PAIS,
	   PPESSOA.CPF,
	   PPESSOA.TELEFONE1,
	   PPESSOA.CARTIDENTIDADE,
	   PPESSOA.UFCARTIDENT,
	   PPESSOA.ORGEMISSORIDENT,
	   PPESSOA.DTEMISSAOIDENT,
	   PPESSOA.TITULOELEITOR,
	   PPESSOA.ZONATITELEITOR,
	   PPESSOA.SECAOTITELEITOR,
	   PPESSOA.CARTEIRATRAB,
	   PPESSOA.SERIECARTTRAB,
	   PPESSOA.UFCARTTRAB,
	   PPESSOA.DTCARTTRAB,
	   PPESSOA.CARTMOTORISTA,
	   PPESSOA.TIPOCARTHABILIT,
	   PPESSOA.DTVENCHABILIT,
	   PPESSOA.CERTIFRESERV,
	   PPESSOA.CATEGMILITAR,
	   PPESSOA.NATURALIDADE,
	   PPESSOA.ESTADONATAL,
	   PPESSOA.DATACHEGADA,
	   PPESSOA.EMAIL,
	   PPESSOA.CORRACA,
	   PPESSOA.DEFICIENTEFISICO,
	   PPESSOA.TIPOSANG,
	   PPESSOA.DATAPRIMEIRACNH,
	   PPESSOA.TELEFONE2,
	   PPESSOA.ORGEMISSORCNH,
	   PPESSOA.DTEMISSAOCNH,
	   PPESSOA.UFCNH,
	   (SELECT MAX(PFDEPEND.NOME) FROM [dbo].PFDEPEND WHERE PFUNC.CHAPA = PFDEPEND.CHAPA AND PFDEPEND.GRAUPARENTESCO = '6' AND PFUNC.CODCOLIGADA = PFDEPEND.CODCOLIGADA) AS PAI,
	   (SELECT MAX(PFDEPEND.NOME) FROM [dbo].PFDEPEND WHERE PFUNC.CHAPA = PFDEPEND.CHAPA AND PFDEPEND.GRAUPARENTESCO = '7' AND PFUNC.CODCOLIGADA = PFDEPEND.CODCOLIGADA) AS MAE,
	   (SELECT MAX(PFDEPEND.NOME) FROM [dbo].PFDEPEND WHERE PFUNC.CHAPA = PFDEPEND.CHAPA AND PFDEPEND.GRAUPARENTESCO = '5' AND PFUNC.CODCOLIGADA = PFDEPEND.CODCOLIGADA) AS CONJUGUE,
	   PSECAO.ID AS SERVICO,
	   PFUNCAO.ID AS CARGO,
	   (SELECT max(GDEPTO.CODDEPARTAMENTO) FROM [dbo].GDEPTO WHERE GDEPTO.CODDEPARTAMENTO = PSECAO.CODDEPTO AND PSECAO.CODCOLIGADA = GDEPTO.CODCOLIGADA) AS DEPTO,
	   (SELECT top 1 PFMOVCC.CODCCUSTO FROM [dbo].PFMOVCC WHERE PFMOVCC.CODCOLIGADA = PFUNC.CODCOLIGADA AND PFMOVCC.CHAPA = PFUNC.CHAPA ORDER BY PFMOVCC.ANOCOMP, PFMOVCC.MESCOMP, PFMOVCC.CODEVENTO) AS CCUSTO
FROM [dbo].PFUNC
LEFT JOIN [dbo].PSECAO
ON PFUNC.CODSECAO = PSECAO.CODIGO
AND PFUNC.CODCOLIGADA = PSECAO.CODCOLIGADA
LEFT JOIN [dbo].PPESSOA
ON PFUNC.CODPESSOA = PPESSOA.CODIGO
LEFT JOIN [dbo].PFUNCAO 
ON PFUNC.CODCOLIGADA = PFUNCAO.CODCOLIGADA
AND PFUNC.CODFUNCAO = PFUNCAO.CODIGO
ORDER BY 1, 2