SELECT PSECAO.CODCOLIGADA,
       PSECAO.ID,
       PSECAO.DESCRICAO,
       PSECAO.CGC,
       PSECAO.CEI,
       PSECAO.FPAS,
       PSECAO.INSCRESTADUAL,
       PSECAO.INSCRMUNICIPAL,
       PSECAO.RUA,
       PSECAO.NUMERO,
       PSECAO.COMPLEMENTO,
       PSECAO.BAIRRO,
       PSECAO.ESTADO,
       PSECAO.CIDADE,
       PSECAO.NAOEMPREGPROPR,
       PSECAO.CATEGORIA,
       PSECAO.CODTERCEIROSINSS,
       PSECAO.PERCENTTERCEIROS,
       PSECAO.PERCENTACIDTRAB,
       PSECAO.CODFILIAL,
       PSECAO.CODPAGTOGPS,
       PSECAO.PERCISENCAOFILANTROPIA,
       PSECAO.CEP,
       PSECAO.CNAESEFIP,
       (SELECT MIN(PFUNC.DATAADMISSAO) FROM [dbo].PFUNC WHERE PSECAO.CODCOLIGADA = PFUNC.CODCOLIGADA) AS VIGENCIA
  FROM [dbo].PSECAO
 ORDER BY 1, 2