SELECT TB.BDCODBAN,
       TB.BDNOMBAN,
	   TBA.BDCODAG,
	   TBA.BDCODCID,
	   TBA.BDNOMAG,
	   TBA.BDCODAGBAN,
	   TBA.BDCODAGBANDV,
	   TBA.BDCEPAG,
	   TBA.BDLOGRAG,
	   TBA.BDNUMAG,
	   TBA.BDCOMPLAG,
	   TBA.BDBAIAG
FROM TBANCOS AS TB
INNER JOIN TBANCOS_AG AS TBA
ON (TB.BDCODBAN = TBA.BDCODBAN)
ORDER BY TB.BDCODBAN