SELECT DISTINCT vetv.BDCODEMP,
       vetv.BDCODTFUC,
       vft.BDDESCTFUC,
       vft.BDCODCBO
  FROM VRH_EMP_TCOL_VINCDIA vetv
  LEFT JOIN VRH_FUNCOES_TFUC vft
    ON vft.BDCODPLAFUC = vetv.BDCODPLAFUC
   AND vft.BDCODTFUC = vetv.BDCODTFUC
 WHERE vetv.BDCODTFUC IS NOT NULL
 ORDER BY vetv.BDCODEMP, vetv.BDCODTFUC