SELECT TER.BDCODEMP,
	   TER.BDREFEMP,
	   TC.BDCODCENTRO,
	   TC.BDNOME
FROM TCENTROS AS TC
INNER JOIN TEMPRESAS_REF AS TER
ON (TC.BDCODCENTRO = TER.BDCODCENTROS)
WHERE TER.BDREFEMP = (SELECT MAX(TEMPRESAS_REF.BDREFEMP) FROM TEMPRESAS_REF WHERE TEMPRESAS_REF.BDCODCENTROS = TC.BDCODCENTRO)
ORDER BY TER.BDCODEMP