SELECT VRH_EMP_TDEP.BDCODEMP,
       VRH_EMP_TDEP.BDCODCOL,
	   VRH_EMP_TDEP.BDCODDPN,
	   VRH_EMP_TDEP.BDCODCID,
	   VRH_EMP_TDEP.BDCARTDPN,
	   VRH_EMP_TDEP.BDREGDPN,
	   VRH_EMP_TDEP.BDLIVDPN,
	   VRH_EMP_TDEP.BDFOLDPN,
	   VRH_EMP_TDEP_REF.BDREFDPN,
	   VRH_EMP_TDEP_REF.BDPARENESCODPN,
	   VRH_EMP_TDEP_REF.BDNOMEDPN,
	   VRH_EMP_TDEP_REF.BDDATANASCDPN,
	   VRH_EMP_TDEP_REF.BDCPFDPN,
	   VRH_EMP_TDEP_REF.BDSALFAMDPN,
	   VRH_EMP_TDEP_REF.BDDEPIRDPN
FROM VRH_EMP_TDEP
INNER JOIN VRH_EMP_TDEP_REF
ON (VRH_EMP_TDEP.BDCODEMP = VRH_EMP_TDEP_REF.BDCODEMP AND VRH_EMP_TDEP.BDCODCOL = VRH_EMP_TDEP_REF.BDCODCOL AND VRH_EMP_TDEP.BDCODDPN = VRH_EMP_TDEP_REF.BDCODDPN)
WHERE VRH_EMP_TDEP_REF.BDREFDPN = (SELECT MAX(VRH_EMP_TDEP_REF.BDREFDPN) 
                                   FROM VRH_EMP_TDEP_REF
								   WHERE VRH_EMP_TDEP_REF.BDCODEMP = VRH_EMP_TDEP.BDCODEMP 
								   AND VRH_EMP_TDEP_REF.BDCODCOL = VRH_EMP_TDEP.BDCODCOL 
								   AND VRH_EMP_TDEP_REF.BDCODDPN = VRH_EMP_TDEP.BDCODDPN)
ORDER BY VRH_EMP_TDEP.BDCODEMP, VRH_EMP_TDEP.BDCODCOL, VRH_EMP_TDEP.BDCODDPN
	   