SELECT VRH_EMP_TCOLCON.BDCODEMP,
       VRH_EMP_TCOLCON.BDCODCOL,
       VRH_EMP_TCOLCON.BDCODCIDNASC,
       VRH_EMP_TCOLCON.BDDATANASCCOL,
       VRH_EMP_TCOLCON.BDDATAADMCOL,
       VRH_EMP_TCOLCON.BDCODTIPO,
       VRH_EMP_TCOLCON.BDCPFCOL,
       VRH_EMP_TCOLCON.BDDTPCOL,
       VRH_EMP_TCOLCON.BDCONTRCOL,
       VRH_EMP_TCOLCON.BDMATRICULA,
       VRH_EMP_TCOLCON.BDMATRICULASEMVINC,
       VRH_EMP_TCOLCON_CAD.BDREFCOL,
       VRH_EMP_TCOLCON_CAD.BDNOMCOL,
       VRH_EMP_TCOLCON_CAD.BDCODPAISRES,
       VRH_EMP_TCOLCON_CAD.BDCEPCOL,
       VRH_EMP_TCOLCON_CAD.BDLOGRCOL,
       VRH_EMP_TCOLCON_CAD.BDENDCOL,
       VRH_EMP_TCOLCON_CAD.BDNUMCOL,
       VRH_EMP_TCOLCON_CAD.BDCOMPLCOL,
       VRH_EMP_TCOLCON_CAD.BDBAICOL,
       VRH_EMP_TCOLCON_CAD.BDCODCID,
       VRH_EMP_TCOLCON_CAD.BDDDDCOL,
       VRH_EMP_TCOLCON_CAD.BDFONECOL,
       VRH_EMP_TCOLCON_CAD.BDDDDCELCOL,
       VRH_EMP_TCOLCON_CAD.BDCELCOL,
       VRH_EMP_TCOLCON_CAD.BDEMAILCOL,
       VRH_EMP_TCOLCON_CAD.BDGRAUINSTCOL,
       VRH_EMP_TCOLCON_CAD.BDESTCIVCOL,
       VRH_EMP_TCOLCON_CAD.BDSEXOCOL,
       VRH_EMP_TCOLCON_CAD.BDDATACHEBRASILCOL,
       VRH_EMP_TCOL_PARENT.BDNOMPAICOL,
       VRH_EMP_TCOL_PARENT.BDNOMMAECOL,
       VRH_EMP_TCOL_PARENT.BDNOMCONJCOL,
	   VRH_EMP_TCOL_ADM.BDCNPJEMPANT,
       VRH_EMP_TCOLCON_DOC.BDRESERVISTACOL,
       VRH_EMP_TRESCISAO.BDDATARESCISAO,
       VRH_EMP_TRESCISAO.BDCODPRES,
       VRH_EMP_TCOL_ADM.BDTIPOADMCOL,
	   VRHF_EMP_TCOLCON_BANCO.BDCODBAN,
	   VRHF_EMP_TCOLCON_BANCO.BDCODAG,
	   VRHF_EMP_TCOLCON_BANCO.BDCTADEPCOL,
	   VRHF_EMP_TCOLCON_BANCO.BDDIGCTADEPCOL,
	   VRHF_EMP_TCOLCON_BANCO.BDTIPOCTACOL,
	   VRHF_EMP_TCOLCON_BANCO.BDMODOPAGTOCOL,
	   VRH_EMP_TTRANSCOL.BDDATATRF,
	   VRH_EMP_TCOL_ADM.BDDIASPRAZO1COL,
	   VRH_EMP_TCOL_ADM.BDDIASPRAZO2COL
  FROM VRH_EMP_TCOLCON
 LEFT JOIN VRH_EMP_TTRANSCOL
    ON VRH_EMP_TTRANSCOL.BDCODEMPDEST = VRH_EMP_TCOLCON.BDCODEMP
    AND VRH_EMP_TTRANSCOL.BDCODCOLDEST = VRH_EMP_TCOLCON.BDCODCOL
 LEFT JOIN VRHF_EMP_TCOLCON_BANCO
    ON VRHF_EMP_TCOLCON_BANCO.BDCODEMP = VRH_EMP_TCOLCON.BDCODEMP
   AND VRHF_EMP_TCOLCON_BANCO.BDCODCOL = VRH_EMP_TCOLCON.BDCODCOL
   AND VRHF_EMP_TCOLCON_BANCO.BDREFCOL = (SELECT MAX(VRHF_EMP_TCOLCON_BANCO.BDREFCOL)
                                         FROM VRHF_EMP_TCOLCON_BANCO
                                        WHERE VRHF_EMP_TCOLCON_BANCO.BDCODEMP = VRH_EMP_TCOLCON.BDCODEMP
                                          AND VRHF_EMP_TCOLCON_BANCO.BDCODCOL = VRH_EMP_TCOLCON.BDCODCOL
                                          AND VRHF_EMP_TCOLCON_BANCO.BDCODCOL = VRHF_EMP_TCOLCON_BANCO.BDCODCOL)
 INNER JOIN VRH_EMP_TCOLCON_DOC
    ON VRH_EMP_TCOLCON_DOC.BDCODEMP = VRH_EMP_TCOLCON.BDCODEMP
   AND VRH_EMP_TCOLCON_DOC.BDCODCOL = VRH_EMP_TCOLCON.BDCODCOL
 INNER JOIN VRH_EMP_TCOL_PARENT
    ON VRH_EMP_TCOL_PARENT.BDCODEMP = VRH_EMP_TCOLCON.BDCODEMP
   AND VRH_EMP_TCOL_PARENT.BDCODCOL = VRH_EMP_TCOLCON.BDCODCOL
 INNER JOIN VRH_EMP_TCOL_ADM
    ON VRH_EMP_TCOL_ADM.BDCODEMP = VRH_EMP_TCOLCON.BDCODEMP
   AND VRH_EMP_TCOL_ADM.BDCODCOL = VRH_EMP_TCOLCON.BDCODCOL
  LEFT JOIN VRH_EMP_TRESCISAO
    ON VRH_EMP_TRESCISAO.BDCODEMP = VRH_EMP_TCOLCON.BDCODEMP
   AND VRH_EMP_TRESCISAO.BDCODCOL = VRH_EMP_TCOLCON.BDCODCOL
 INNER JOIN VRH_EMP_TCOLCON_CAD
    ON VRH_EMP_TCOLCON_CAD.BDCODEMP = VRH_EMP_TCOLCON.BDCODEMP
   AND VRH_EMP_TCOLCON_CAD.BDCODCOL = VRH_EMP_TCOLCON.BDCODCOL
 WHERE VRH_EMP_TCOLCON_CAD.BDREFCOL = (SELECT MAX(VRH_EMP_TCOLCON_CAD.BDREFCOL)
                                         FROM VRH_EMP_TCOLCON_CAD
                                        WHERE VRH_EMP_TCOLCON_CAD.BDCODEMP = VRH_EMP_TCOLCON.BDCODEMP
                                          AND VRH_EMP_TCOLCON_CAD.BDCODCOL = VRH_EMP_TCOLCON.BDCODCOL
                                          AND VRH_EMP_TCOLCON_CAD.BDCODCOL = VRH_EMP_TCOLCON_CAD.BDCODCOL)
ORDER BY VRH_EMP_TCOLCON.BDCODEMP,
          VRH_EMP_TCOLCON.BDCODCOL