SELECT TEMPRESAS.BDCODEMP,
           VRH_BASE_TVERBAS.BDCODVER,
           VRH_BASE_TVERBAS.BDDESCVER,
           VRH_BASE_TVERBAS.BDCLAVER 
FROM TEMPRESAS,
     VRH_BASE_TVERBAS
WHERE (LEFT(BDCLAVER,7) IN ('0.001.3')
    OR LEFT(BDCLAVER,5) IN ('0.006')
        OR LEFT(BDCLAVER,9) IN ('0.005.0.1','0.005.0.2','0.005.0.4')
        OR LEFT(BDCLAVER,13) IN ('0.001.1.0.001','0.002.0.2.002'))
    AND EXISTS(SELECT FIRST 1 VWRH_UNION_MES_VERBAS.BDCODVER
                 FROM VWRH_UNION_MES_VERBAS
                WHERE VWRH_UNION_MES_VERBAS.BDCODVER = VRH_BASE_TVERBAS.BDCODVER
                  AND VWRH_UNION_MES_VERBAS.BDCODEMP = TEMPRESAS.BDCODEMP)