SELECT VRH_BASE_TQH_HORARIO.BDCODQHH,
       VRH_BASE_TQH_HORARIO.BDDESQHH,

	   (SELECT BDQHHSENT 
	    FROM VRH_BASE_TQH_HORAS 
		WHERE VRH_BASE_TQH_HORARIO.BDCODQHH = BDCODQHH 
		AND BDCODQHHS = 1) AS HORA_ENTRADA,
		
	   (SELECT BDQHHSSAI 
	    FROM VRH_BASE_TQH_HORAS 
		WHERE VRH_BASE_TQH_HORARIO.BDCODQHH = BDCODQHH 
		AND BDCODQHHS = 1) AS HORA_SAIDA_INTERVALO,
		
	   (SELECT BDQHHSENT 
	    FROM VRH_BASE_TQH_HORAS 
		WHERE VRH_BASE_TQH_HORARIO.BDCODQHH = BDCODQHH 
		AND BDCODQHHS = 2) as HORA_ENTRADA_INTERVALO,
		
	   (SELECT BDQHHSSAI 
	    FROM VRH_BASE_TQH_HORAS 
		WHERE VRH_BASE_TQH_HORARIO.BDCODQHH = BDCODQHH 
		AND BDCODQHHS = 2) AS HORA_SAIDA
	   
FROM VRH_BASE_TQH_HORARIO
ORDER BY BDCODQHH
