SELECT VRH_BASE_TSINDTRAB.*,
       VRH_BASE_TSINDTREF.BDDATBASEST,
	   VRH_BASE_TSINDTREF.BDREFST
FROM VRH_BASE_TSINDTRAB
INNER JOIN VRH_BASE_TSINDTREF
ON (VRH_BASE_TSINDTRAB.BDCODSINTRAB = VRH_BASE_TSINDTREF.BDCODSINTRAB)
WHERE VRH_BASE_TSINDTREF.BDREFST = (SELECT MAX(VRH_BASE_TSINDTREF.BDREFST) FROM VRH_BASE_TSINDTREF WHERE VRH_BASE_TSINDTREF.BDCODSINTRAB = VRH_BASE_TSINDTRAB.BDCODSINTRAB)
ORDER BY VRH_BASE_TSINDTRAB.BDCODSINTRAB