SELECT 
    BDCODEMP,
    BDCODCOL,
    BDDATACOL,
    BDCODMAS,
    BDSALBASECOL,
    BDCONTRCOL,
    BDDESCSALVAR,
    BDDATAEFEITO,
    BDREFCOL,
    BDREFEFEITOCOL,
    CASE 
        WHEN BDSALBASECOL <= 150 THEN 5
        WHEN BDSALBASECOL > 150 THEN 1
    END AS CATEGORIA
FROM 
    VRH_EMP_TCOLCON_CALCDIA
ORDER BY 
    BDCODEMP, BDCODCOL, BDDATACOL;