SELECT VWRH_UNION_MES_VERBAS.BDCODEMP,
	   VWRH_UNION_MES_VERBAS.BDCODCOL,
	   VWRH_UNION_MES_VERBAS.BDCODVER,
	   VWRH_UNION_MES_VERBAS.BDREFFN,
	   VRH_BASE_TVERBAS.BDCLAVER,
	   VWRH_UNION_MES_VERBAS.BDREFVFN_MAX,
	   VWRH_UNION_MES_VERBAS.BDREFAUXVFN_MAX,
	   VWRH_UNION_MES_VERBAS.BDVALVFN
  FROM VWRH_UNION_MES_VERBAS 
INNER JOIN VRH_BASE_TVERBAS
        ON VWRH_UNION_MES_VERBAS.BDCODVER = VRH_BASE_TVERBAS.BDCODVER
WHERE (LEFT(BDCLAVER,7) IN ('0.001.3')
   OR LEFT(BDCLAVER,5) IN ('0.006')
   OR LEFT(BDCLAVER,9) IN ('0.005.0.1','0.005.0.2','0.005.0.4')
   OR LEFT(BDCLAVER,13) IN ('0.001.1.0.001','0.002.0.2.002') )
   AND LEFT(BDREFFN,4) >= EXTRACT(YEAR FROM CURRENT_DATE) -2
ORDER BY 1,2,4,3