SELECT EMPRESA,
       TABELA,
       CHAVE,
       COD_TABELA,
       RECIBO_INCLUSAO,
       INI_VALID
  FROM CONTROLE_E_SOCIAL
 WHERE DATA_EXCLUSAO IS NULL
   AND ID_EXCLUIR IS NULL
   AND AMBIENTE = '1'
   AND RECIBO_INCLUSAO IS NOT NULL
   AND COD_TABELA IN ('1000', '1005', '1020', '1030', '1050', '2200', '2206', '2300')
 ORDER BY EMPRESA,
          COD_TABELA,
          CHAVE;