SELECT l.EMPRESA,
       l.EMPREGADO,
       l.ANO || '-' || LPAD(l.MES, 2, '0') || '-01' AS COMPETENCIA,
       l.VERBA,
       l.VALORLANCADO,
       l.VALORCALCULADO,
       'NORMAL' AS TIPO
  FROM LANCAMENTO l
 WHERE l.ANO < 3000
   AND l.ANO >= EXTRACT(YEAR FROM CURRENT_DATE) - 3
UNION ALL
SELECT l.EMPRESA,
       l.EMPREGADO,
       l.ANO || '-' || LPAD(l.MES, 2, '0') || '-01' AS COMPETENCIA,
       l.VERBA,
       l.VALORLANCADO,
       l.VALORREFLEXO AS VALORCALCULADO,
       'DSR' AS TIPO
  FROM LANCAMENTO l
 WHERE l.ANO < 3000
   AND l.ANO >= EXTRACT(YEAR FROM CURRENT_DATE) - 3
   AND l.VALORREFLEXO  > 0
 ORDER BY 1,
          2,
          3,
          4,
          6 DESC