SELECT EMPRESA,
       EMPREGADO,
       AQUISICAOINICIO,
       AQUISICAOFINAL,
       DATEADD(DAY, -1, DATEADD(YEAR, 2, AQUISICAOINICIO)) DATA_LIMITE,
       SUM(QTDDIAS) AS DIAS_GOZADO,
       SUM(DATEDIFF(DAY, ABONOINICIO, ABONOFINAL)) + 1 AS DIAS_ABONO
  FROM FERIAS
 GROUP BY EMPRESA,
          EMPREGADO,
          AQUISICAOINICIO,
          AQUISICAOFINAL,
          DATA_LIMITE,
          FALTAS
 ORDER BY EMPRESA,
          EMPREGADO,
          AQUISICAOINICIO;