SELECT (SELECT CD_EMPRESA FROM CAD_EMPRESACONFIG WHERE CAD_EMPRESACONFIG.ID_EMPRESA = CAD_DEPENDENTE.ID_EMPRESA ) AS CODI_EMP,
	CAD_DEPENDENTE.CD_FUNCIONARIO,
	CAD_DEPENDENTE.ID_EMPRESA,
	CAD_DEPENDENTE.ID_DEPENDENTE,
	CAD_DEPENDENTE.DS_NOME,
	CAD_DEPENDENTE.DT_NASCIMENTO,
	CAD_DEPENDENTE.NR_CPF,
	CAD_DEPENDENTE.NR_TELEFONE,
	CAD_DEPENDENTE.DS_REGISTRO,
	CAD_DEPENDENTE.DS_PARENTESCO,
	CAD_DEPENDENTE.FG_DEPENDENTESALARIOFAMILIA,
	CAD_DEPENDENTE.DT_VIGENCIAINICIOSF,
	CAD_DEPENDENTE.DT_VIGENCIAFIMSF,
	CAD_DEPENDENTE.FG_DEPENDENTEIR,
	CAD_DEPENDENTE.DT_VIGENCIAINICIOIR,
	CAD_DEPENDENTE.DT_VIGENCIAFIMIR
FROM CAD_DEPENDENTE
ORDER BY 1,2,3,4