SELECT (SELECT CD_EMPRESA FROM CAD_EMPRESACONFIG WHERE CAD_EMPRESACONFIG.ID_EMPRESA = CAD_DEPENDENTEAUTONOMO.ID_EMPRESA ) AS CODI_EMP,
	CAD_DEPENDENTEAUTONOMO.CD_AUTONOMO,
	CAD_DEPENDENTEAUTONOMO.ID_EMPRESA,
	CAD_DEPENDENTEAUTONOMO.ID_DEPENDENTE,
	CAD_DEPENDENTEAUTONOMO.DS_NOME,
	CAD_DEPENDENTEAUTONOMO.DT_NASCIMENTO,
	CAD_DEPENDENTEAUTONOMO.NR_CPF,
	CAD_DEPENDENTEAUTONOMO.NR_TELEFONE,
	CAD_DEPENDENTEAUTONOMO.DS_REGISTRO,
	CAD_DEPENDENTEAUTONOMO.DS_PARENTESCO,
	NULL,
	NULL,
	NULL,
	CAD_DEPENDENTEAUTONOMO.FG_DEPENDENTEIR,
	CAD_DEPENDENTEAUTONOMO.DT_VIGENCIAINICIOIR,
	CAD_DEPENDENTEAUTONOMO.DT_VIGENCIAFIMIR
FROM CAD_DEPENDENTEAUTONOMO
ORDER BY 1,2,3,4