SELECT (SELECT CD_EMPRESA FROM CAD_EMPRESACONFIG WHERE CAD_EMPRESACONFIG.ID_EMPRESA = CAD_DEPENDENTESOCIO.ID_EMPRESA ) AS CODI_EMP,
	CAD_DEPENDENTESOCIO.CD_SOCIO,
	CAD_DEPENDENTESOCIO.ID_EMPRESA,
	CAD_DEPENDENTESOCIO.ID_DEPENDENTE,
	CAD_DEPENDENTESOCIO.DS_NOME,
	CAD_DEPENDENTESOCIO.DT_NASCIMENTO,
	CAD_DEPENDENTESOCIO.NR_CPF,
	CAD_DEPENDENTESOCIO.NR_TELEFONE,
	CAD_DEPENDENTESOCIO.DS_REGISTRO,
	CAD_DEPENDENTESOCIO.DS_PARENTESCO,
	NULL,
	NULL,
	NULL,
	CAD_DEPENDENTESOCIO.FG_DEPENDENTEIR,
	CAD_DEPENDENTESOCIO.DT_VIGENCIAINICIOIR,
    CAD_DEPENDENTESOCIO.DT_VIGENCIAFIMIR
FROM CAD_DEPENDENTESOCIO
ORDER BY 1,2,3,4