SELECT CD_EMPRESA AS CODI_EMP,
	CAD_EMPRESAFILIAL.ID_EMPRESA,
	CAD_EMPRESAFILIAL.DS_RAZAOSOCIAL,
	CAD_EMPRESAFILIAL.DS_NOMEFANTASIA,
	CAD_EMPRESAFILIAL.DS_LOGRADOURO,
	CAD_EMPRESAFILIAL.NR_NUMERO,
	CAD_EMPRESAFILIAL.DS_COMPLEMENTO,
	CAD_EMPRESAFILIAL.DS_BAIRRO,
	(SELECT DS_MUNICIPIO FROM CAD_MUNICIPIO WHERE CAD_MUNICIPIO.CD_MUNICIPIO = CAD_EMPRESAFILIAL.CD_MUNICIPIO ) AS MUNICIPIO,	
	CAD_EMPRESAFILIAL.CD_UF,
	CAD_EMPRESAFILIAL.NR_CEP,
	CAD_EMPRESAFILIAL.NR_TELEFONE,
	CAD_EMPRESAFILIAL.DS_CONTATO,
	CAD_EMPRESAFILIAL.DS_HOMEPAGE,
	CAD_EMPRESAFILIAL.DS_EMAIL,
	CAD_EMPRESAFILIAL.FG_TIPOCNPJCEI,
	CAD_EMPRESAFILIAL.NR_CNPJCEI,
	CAD_EMPRESAFILIAL.NR_CPF,
	CAD_EMPRESAFILIAL.CD_CNAEFISCAL,
	CAD_EMPRESAFILIAL.CD_NATUREZAJURIDICA,
	CAD_EMPRESAFILIAL.NR_INSCRICAOESTADUAL,
	CAD_EMPRESAFILIAL.DS_ATIVIDADEESTABELECIMENTO,
	DT_ABERTURA,
	CD_FILIAL
FROM CAD_EMPRESAFILIAL LEFT JOIN CAD_EMPRESACONFIG on (CAD_EMPRESAFILIAL.ID_EMPRESA = CAD_EMPRESACONFIG.ID_EMPRESA)
WHERE DT_VIGENCIAINICIO = (SELECT MAX(F.DT_VIGENCIAINICIO)                                                                                                                                          
							FROM CAD_EMPRESAFILIAL AS F
							WHERE F.ID_EMPRESA = CAD_EMPRESAFILIAL.ID_EMPRESA)
ORDER BY 1,2