SELECT emp.CD_EMPRESA AS CODI_EMP,
	   emp.ID_EMPRESA,
       fer.CD_FUNCIONARIO,
       fer.ID_FERIAS,
       fer.DT_PERIODOAQUISITIVOINICIO,
       fer.DT_PERIODOAQUISITIVOFIM,
       fer.NR_QUANTIDADEFALTAS,
       fer.NR_QUANTIDADEDIASDIREITO
  FROM CAD_FERIAS fer
  LEFT JOIN CAD_EMPRESACONFIG emp
    ON fer.ID_EMPRESA = emp.ID_EMPRESA
ORDER BY 1,2,3,5