SELECT emp.CD_EMPRESA AS CODI_EMP,
	   emp.ID_EMPRESA,
       fer.CD_FUNCIONARIO,
       fer.ID_FERIAS,
       fer.ID_PERIODOFERIAS,
       fer.DT_INICIOABONOPECUNIARIO,
       fer.DT_FIMABONOPECUNIARIO,
       fer.NR_DIASGOZO,
       fer.DT_GOZOINICIO,
       fer.DT_GOZOFIM,
       cf.DT_PERIODOAQUISITIVOINICIO,
       cf.DT_PERIODOAQUISITIVOFIM,
       fer.NR_DIASDESCONSIDERAR
  FROM LCT_FERIAS fer
  LEFT JOIN CAD_EMPRESACONFIG emp
    ON fer.ID_EMPRESA = emp.ID_EMPRESA
  LEFT JOIN CAD_FERIAS cf 
    ON fer.ID_EMPRESA = cf.ID_EMPRESA
   AND fer.CD_FUNCIONARIO = cf.CD_FUNCIONARIO 
   AND fer.ID_FERIAS = cf.ID_FERIAS 
  WHERE fer.DT_GOZOINICIO IS NOT NULL  
 ORDER BY 1,2,3,9