SELECT emp.CD_EMPRESA AS CODI_EMP,
	   emp.ID_EMPRESA,
       jor_hor.*
  FROM CAD_QUADROHORARIOEMPRESA jor_emp
  LEFT OUTER JOIN CAD_QUADROHORARIODETALHE jor_hor
    ON jor_hor.CD_QUADROHORARIO = jor_emp.CD_QUADROHORARIO
  LEFT JOIN CAD_EMPRESACONFIG emp
    ON jor_emp.ID_EMPRESA = emp.ID_EMPRESA
 ORDER BY 1,2