SELECT CAD_SINDICATOEMPREGADO.CD_SINDICATOEMPREGADO,
	CAD_SINDICATOEMPREGADO.DS_SINDICATO,
	CAD_SINDICATOEMPREGADO.DS_LOGRADOURO,
	CAD_SINDICATOEMPREGADO.NR_NUMERO,
	CAD_SINDICATOEMPREGADO.DS_COMPLEMENTO,
	CAD_SINDICATOEMPREGADO.DS_BAIRRO,
	CAD_SINDICATOEMPREGADO.CD_UF,
	(SELECT DS_MUNICIPIO FROM CAD_MUNICIPIO WHERE CAD_MUNICIPIO.CD_MUNICIPIO = CAD_SINDICATOEMPREGADO.CD_MUNICIPIO ) AS MUNICIPIO,
	CAD_SINDICATOEMPREGADO.NR_CEP,
	CAD_SINDICATOEMPREGADO.NR_TELEFONE,
	CAD_SINDICATOEMPREGADO.NR_CNPJ,
	CAD_SINDICATOEMPREGADO.CD_ENTIDADE,
	CAD_SINDICATOEMPREGADO.DS_SIGLA,
	CAD_SINDICATOEMPREGADO.NR_MESDISSIDIO,
	CAD_SINDICATOEMPREGADO.DS_HOMEPAGE,
	CAD_SINDICATOEMPREGADO.DS_EMAIL,
	CAD_SINDICATOEMPREGADO.DS_RESPONSAVEL
FROM CAD_SINDICATOEMPREGADO
ORDER BY 1