SELECT (SELECT CD_EMPRESA FROM CAD_EMPRESACONFIG WHERE CAD_EMPRESACONFIG.ID_EMPRESA = CAD_ALTERACAOCARGOSALARIO.ID_EMPRESA ) AS CODI_EMP,
	CAD_ALTERACAOCARGOSALARIO.ID_EMPRESA,
	CAD_ALTERACAOCARGOSALARIO.CD_FUNCIONARIO,
	CAD_ALTERACAOCARGOSALARIO.DT_VIGENCIAINICIO,
	CAD_ALTERACAOCARGOSALARIO.DS_PROFISSAO
FROM CAD_ALTERACAOCARGOSALARIO
WHERE FG_TIPOCADASTRO = 'ALTERACAO SALARIAL'
ORDER BY 1,2,3,4